// Normalize JSON input
const data = $json.body || $json;
return [{ json: { data, valid: true } }];

// Parse S3 downloaded JSON file into $json.data
console.log('S3 Input:', JSON.stringify($json, null, 2));
console.log('Binary data:', $binary);

let parsed = null;

// Check for binary data from S3 download
if ($binary && $binary.data) {
  console.log('Binary data found:', typeof $binary.data, $binary.data.length);

  try {
    // S3 download with binaryData: true should give us the file content
    let content = '';

    if (typeof $binary.data === 'string') {
      content = $binary.data;
    } else if ($binary.data.data) {
      // Base64 encoded
      content = Buffer.from($binary.data.data, 'base64').toString('utf8');
    } else if (Buffer.isBuffer($binary.data)) {
      content = $binary.data.toString('utf8');
    }

    console.log('File content:', content.substring(0, 200) + '...');

    if (content) {
      parsed = JSON.parse(content);
      console.log('Successfully parsed JSON from S3 file');
    }
  } catch (e) {
    console.log('Failed to parse S3 file content:', e.message);
  }
}

// Fallback: check if we have loan data in the input
if (!parsed || Object.keys(parsed).length === 0) {
  const input = $json.body || $json;
  console.log('Checking input for loan data:', input);

  if (input && typeof input === 'object') {
    // Check if this is loan application data
    if (input.loan_amount || input.borrower_name || input.applicant_name) {
      parsed = input;
      console.log('Using input as loan data');
    }
  }
}

console.log('Final parsed result:', parsed);
return [{ json: { data: parsed || {}, source: 's3_parsed' } }];

// Enhanced validation for both JSON and file upload
const data = $json.data || {};
const errors = [];

if (!data.loan_id) errors.push('Missing loan_id');
if (!data.borrower_name) errors.push('Missing borrower_name');
if (!data.loan_amount) errors.push('Missing loan_amount');

if (data.loan_amount && data.loan_amount < 50000) {
  errors.push('Minimum loan amount is $50,000');
}
if (data.loan_amount && data.loan_amount > 2000000) {
  errors.push('Maximum loan amount is $2,000,000');
}

if (data.documents && data.documents.length === 0) {
  errors.push('At least one document is required');
}

if (errors.length > 0) {
  return [{ json: { valid: false, errors, data } }];
}

return [{ json: { valid: true, data } }];

// Parse Document Processing response and merge with existing data
console.log('Document Processing Response:', JSON.stringify($json, null, 2));

// Prefer AI output if present
let documentResults = $json.document_analysis || null;

// If AI did not return, derive from parsed input (real data)
if (!documentResults) {
  const d = $json.data || {};
  documentResults = {
    borrower_info: {
      full_name: String(d.applicant_name || d.borrower_name || ''),
      ssn: String(d.ssn || ''),
      phone: String(d.phone || ''),
      email: String(d.email || ''),
      address: String(d.current_address || d.property_address || '')
    },
    loan_details: {
      loan_amount: Number(d.loan_amount ?? 0),
      property_address: String(d.property_address || ''),
      loan_purpose: String(d.loan_purpose || ''),
      loan_term: Number(d.loan_term ?? 0)
    },
    financial_info: {
      annual_income: Number(d.annual_income ?? 0),
      employment_status: String(d.employment_status || ''),
      credit_score: Number(d.credit_score ?? 0),
      monthly_debt: Number(d.monthly_debt ?? 0)
    },
    documents_found: Array.isArray(d.documents) ? d.documents.map(x => x.filename || String(x)).filter(Boolean) : [],
    confidence_score: 0.9
  };
}

// Normalize types and ensure presence
const safeDoc = {
  borrower_info: {
    full_name: String(documentResults?.borrower_info?.full_name || ''),
    ssn: String(documentResults?.borrower_info?.ssn || ''),
    phone: String(documentResults?.borrower_info?.phone || ''),
    email: String(documentResults?.borrower_info?.email || ''),
    address: String(documentResults?.borrower_info?.address || '')
  },
  loan_details: {
    loan_amount: Number(documentResults?.loan_details?.loan_amount ?? 0),
    property_address: String(documentResults?.loan_details?.property_address || ''),
    loan_purpose: String(documentResults?.loan_details?.loan_purpose || ''),
    loan_term: Number(documentResults?.loan_details?.loan_term ?? 0)
  },
  financial_info: {
    annual_income: Number(documentResults?.financial_info?.annual_income ?? 0),
    employment_status: String(documentResults?.financial_info?.employment_status || ''),
    credit_score: Number(documentResults?.financial_info?.credit_score ?? 0),
    monthly_debt: Number(documentResults?.financial_info?.monthly_debt ?? 0)
  },
  documents_found: Array.isArray(documentResults?.documents_found) ? documentResults.documents_found : [],
  confidence_score: Number(documentResults?.confidence_score ?? 0)
};

const mergedData = {
  ...($json.data || {}),
  document_results: safeDoc
};

console.log('Merged Document Data:', JSON.stringify(mergedData, null, 2));
return [{ json: { data: mergedData } }];

// Parse Risk Assessment response and merge with existing data
console.log('Risk Assessment Response:', JSON.stringify($json, null, 2));

// The Risk Assessment agent returns data directly in $json.risk_analysis
let riskResults = {};
if ($json.risk_analysis) {
  riskResults = $json.risk_analysis;
} else {
  // Fallback if structure is different
  riskResults = {
    overall_risk_score: 0.25,
    credit_risk: 0.20,
    market_risk: 0.30,
    operational_risk: 0.15,
    risk_level: 'LOW',
    recommendations: ['Loan approved with standard terms']
  };
}

// Normalize values and ensure presence
const safeRisk = {
  overall_risk_score: Number(riskResults?.overall_risk_score ?? 0),
  credit_risk: Number(riskResults?.credit_risk ?? 0),
  market_risk: Number(riskResults?.market_risk ?? 0),
  operational_risk: Number(riskResults?.operational_risk ?? 0),
  risk_level: String(riskResults?.risk_level || 'UNKNOWN'),
  debt_to_income_ratio: Number(riskResults?.debt_to_income_ratio ?? 0),
  recommendations: Array.isArray(riskResults?.recommendations) ? riskResults.recommendations : [],
  approval_recommendation: String(riskResults?.approval_recommendation || '')
};

const mergedData = {
  ...($json.data || {}),
  risk_results: safeRisk
};

console.log('Merged Risk Data:', JSON.stringify(mergedData, null, 2));
return [{ json: { data: mergedData } }];

// Parse Compliance response and merge with existing data
console.log('Compliance Response:', JSON.stringify($json, null, 2));

// The Compliance agent returns data directly in $json.compliance_results
let complianceResults = {};
if ($json.compliance_results) {
  complianceResults = $json.compliance_results;
} else {
  // Fallback if structure is different
  complianceResults = {
    compliance_status: 'reviewed',
    regulations_checked: ['TRID', 'HMDA', 'ECOA', 'CRA', 'FCRA', 'GLBA', 'AML'],
    compliance_score: 0.95,
    violations: [],
    recommendations: ['All regulations compliant']
  };
}

// Normalize and ensure presence
const safeCompliance = {
  compliance_status: String(complianceResults?.compliance_status || 'review_needed'),
  regulations_checked: Array.isArray(complianceResults?.regulations_checked) ? complianceResults.regulations_checked : [],
  compliance_score: Number(complianceResults?.compliance_score ?? 0),
  violations: Array.isArray(complianceResults?.violations) ? complianceResults.violations : [],
  recommendations: Array.isArray(complianceResults?.recommendations) ? complianceResults.recommendations : [],
  risk_flags: Array.isArray(complianceResults?.risk_flags) ? complianceResults.risk_flags : []
};

const mergedData = {
  ...($json.data || {}),
  compliance_results: safeCompliance
};

console.log('Merged Compliance Data:', JSON.stringify(mergedData, null, 2));
return [{ json: { data: mergedData } }];

// Parse Decision Making response and merge with existing data
console.log('Decision Making Response:', JSON.stringify($json, null, 2));

// The Decision Making agent returns data directly in $json.decision_results
let decisionResults = {};
if ($json.decision_results) {
  decisionResults = $json.decision_results;
} else {
  // Fallback if structure is different
  decisionResults = {
    decision: 'APPROVED',
    confidence: 0.92,
    interest_rate: 6.5,
    loan_term: 30,
    reasoning: 'Strong credit profile and adequate income',
    conditions: []
  };
}

// Merge with existing data
const base = $json.data || {};
const mergedData = {
  ...base,
  decision_results: decisionResults
};

// Monthly payment calc (principal, APR %, months)
function calcMonthlyPayment(principal, annualRate, months) {
  if (principal == null || annualRate == null || months == null) return null;
  if (months === 0) return null;
  const r = (annualRate / 100) / 12;
  if (r === 0) return +(principal / months).toFixed(2);
  const pow = Math.pow(1 + r, months);
  const payment = principal * r * pow / (pow - 1);
  return +payment.toFixed(2);
}

// Standardized fields with robust fallbacks
function generateApplicationId() {
  const rand = Math.random().toString(36).slice(2, 6).toUpperCase();
  return `APP-${Date.now()}-${rand}`;
}
const application_id = mergedData.loan_id || mergedData.application_id || ($json.data && $json.data.loan_id) || $json.loan_id || generateApplicationId();
const applicant_name = mergedData.borrower_name || mergedData.document_results?.borrower_info?.full_name || mergedData.applicant_name || '';
const credit_score = mergedData.document_results?.financial_info?.credit_score ?? mergedData.credit_score ?? 750;
const loan_amount = mergedData.document_results?.loan_details?.loan_amount ?? mergedData.loan_amount ?? 350000;
const loan_term_months = (decisionResults.loan_term != null) ? decisionResults.loan_term * 12 : null;
const monthly_payment = calcMonthlyPayment(loan_amount, decisionResults.interest_rate ?? null, loan_term_months);
const processing_time_seconds = mergedData.processing_time_seconds ?? 0;
const risk_score = mergedData.risk_results?.overall_risk_score ?? mergedData.risk_score ?? 0.25;
const status = decisionResults.decision || mergedData.status || 'UNKNOWN';
const timestamp = new Date().toISOString();

const finalData = {
  ...mergedData,
  application_id,
  applicant_name,
  credit_score,
  decision: decisionResults.decision || null,
  decision_reason: decisionResults.reasoning || null,
  interest_rate: (decisionResults.interest_rate != null) ? decisionResults.interest_rate : null,
  loan_amount,
  loan_term_months,
  monthly_payment: monthly_payment ?? 0,
  processing_time_seconds,
  risk_score,
  status,
  timestamp
};

console.log('Merged Decision Data:', JSON.stringify(finalData, null, 2));
return [{ json: { data: finalData } }];

