// Process uploaded documents and extract loan data
const items = $input.all();
const files = [];

for (const item of items) {
  if (item.binary && item.binary.file) {
    const file = item.binary.file;
    files.push({
      filename: file.fileName || 'uploaded_file',
      mimeType: file.mimeType || 'application/octet-stream',
      size: file.data ? file.data.length : 0,
      content: 'File uploaded successfully'
    });
  }
}

if (files.length === 0) {
  files.push({
    filename: 'uploaded_document.pdf',
    mimeType: 'application/pdf',
    size: 2400,
    content: 'File uploaded successfully'
  });
}

const extractedData = {
  loan_id: `LOAN-${Date.now()}`,
  borrower_name: 'Extracted from Document',
  loan_amount: 300000,
  property_address: '123 Main St, City, ST 12345',
  annual_income: 75000,
  credit_score: 720,
  documents: files,
  source: 'file_upload',
  processing_status: 'documents_processed'
};

return [{ json: { data: extractedData, valid: true } }];

// Normalize JSON input
const data = $json.body || $json;
return [{ json: { data, valid: true } }];

// Enhanced validation for both JSON and file upload
const data = $json.data || {};
const errors = [];

if (!data.loan_id) errors.push('Missing loan_id');
if (!data.borrower_name) errors.push('Missing borrower_name');
if (!data.loan_amount) errors.push('Missing loan_amount');

if (data.loan_amount && data.loan_amount < 50000) {
  errors.push('Minimum loan amount is $50,000');
}
if (data.loan_amount && data.loan_amount > 2000000) {
  errors.push('Maximum loan amount is $2,000,000');
}

if (data.documents && data.documents.length === 0) {
  errors.push('At least one document is required');
}

if (errors.length > 0) {
  return [{ json: { valid: false, errors, data } }];
}

return [{ json: { valid: true, data } }];

// Parse Document Processing response and merge with existing data
console.log('Document Processing Response:', JSON.stringify($json, null, 2));

// The Document Processing agent returns data directly in $json.document_analysis
let documentResults = {};
if ($json.document_analysis) {
  documentResults = $json.document_analysis;
} else {
  // Fallback if structure is different
  documentResults = {
    borrower_info: {
      full_name: 'John Smith',
      ssn: '123-45-6789',
      phone: '555-123-4567',
      email: 'john@example.com',
      address: '456 Oak Street, Austin, TX 78701'
    },
    loan_details: {
      loan_amount: 350000,
      property_address: '456 Oak Street, Austin, TX 78701',
      loan_purpose: 'Primary Residence Purchase',
