    loan_term: 30,
    reasoning: 'Strong credit profile and adequate income',
    conditions: []
  };
}

// Merge with existing data
const base = $json.data || {};
const mergedData = {
  ...base,
  decision_results: decisionResults
};

// Monthly payment calc (principal, APR %, months)
function calcMonthlyPayment(principal, annualRate, months) {
  if (!principal || !annualRate || !months) return null;
  const r = (annualRate / 100) / 12;
  if (r === 0) return +(principal / months).toFixed(2);
  const pow = Math.pow(1 + r, months);
  const payment = principal * r * pow / (pow - 1);
  return +payment.toFixed(2);
}

// Standardized fields
const application_id = mergedData.loan_id || mergedData.application_id || $json.loan_id || null;
const applicant_name = mergedData.borrower_name || mergedData.document_results?.borrower_info?.full_name || mergedData.applicant_name || null;
const credit_score = mergedData.document_results?.financial_info?.credit_score ?? mergedData.credit_score ?? null;
const loan_amount = mergedData.document_results?.loan_details?.loan_amount ?? mergedData.loan_amount ?? null;
const loan_term_months = decisionResults.loan_term ? decisionResults.loan_term * 12 : null;
const monthly_payment = calcMonthlyPayment(loan_amount, decisionResults.interest_rate, loan_term_months);
const processing_time_seconds = mergedData.processing_time_seconds ?? 0;
const risk_score = mergedData.risk_results?.overall_risk_score ?? mergedData.risk_score ?? null;
const status = decisionResults.decision || mergedData.status || 'UNKNOWN';
const timestamp = new Date().toISOString();

