  loan_amount,
  loan_term_months,
  monthly_payment,
  processing_time_seconds,
  risk_score,
  status,
  timestamp
};

console.log('Merged Decision Data:', JSON.stringify(finalData, null, 2));
return [{ json: { data: finalData } }];
